import React, { useState, useEffect } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { Switch } from 'antd';

import { BASE_URL } from './config/Api';
import './AdminPage.css';

const AdminPage = () => {

     const username = getUsername();
     console.log(username);

    const [id, setId] = useState('');
    const [upload, setUpload] = useState('');
    const [toggle, setToggle] = useState(false);
    const [title, setTitle] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');

    console.log(upload);
    console.log(toggle);
    console.log(title);
    console.log(price);
    console.log(description);    

       const toggled = () => {
            toggle ? setToggle(false) : setToggle(true);
        }



        const data = { id, title, toggle, price, description } 

    const submitFormUpload = async (event) => {
        event.preventDefault();



        // const files = event.target.files;
        // const data = new FormData();
        // data.append('file', files[0]);



        console.log(data);



     const formUpload = document.querySelector('Form');
        formUpload.addEventListener('submit', (e) => {
            e.preventDefault();

            const request = new XMLHttpRequest();

            var xhr = new XMLHttpRequest;
            xhr.open('POST', '/', true);
            xhr.send()

            request.open('POST', BASE_URL + "/upload/");  

            request.send(JSON.stringify(new FormData(formUpload)));
        });


        console.log(formUpload);
             console.log(new FormData(formUpload)); 



/*       let formData = new FormData();
      formData.append("uploadFiles", fileToUpload)   

      const result = await fetch( BASE_URL + "/upload/"), {
            method: 'POST',
            body: formData
        });

        console.log(formData); 


        const file = await result.json();  */




    }



    return (
        <>
            <h1 className="display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2">Welcome {username} to your admin page</h1>    
      <Container className="">
        <Form onSubmit={submitFormUpload}>
            <h1 className="mb-4">Upload files to Products</h1>
            <input type="file" className="form-control mb-4" name="files"
                onChange={event => setUpload(event.target.files)}
            />
            <div className="d-flex">
                <h6 className="pe-5 me-5">Featured no / yes</h6>
                <Switch className="mb-4" onChange={toggled} name="featured" />
            </div>
            <input type="text" className="form-control mb-4" name="title" placeholder="Title"
                onChange={event => setTitle(event.target.value)}
            />
            <input type="int" className="form-control mb-4" name="price" placeholder="Price"
                onChange={event => setPrice(event.target.value)}
            />
            <textarea type="input" className="form-control mb-4" name="message" rows="6" placeholder="Description"
                onChange={event => setDescription(event.target.value)}
            />
             <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload Info</Button>

        </Form>
        </Container>
        </>
    );

}

export default AdminPage;
