import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router';
import {Container, Row, Form, Button} from 'react-bootstrap'
import { Image } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { getUsername } from './config/Storage';
import { getToken } from './config/Storage';
import displayMessage from './config/displayMessage';


import { BASE_URL } from './config/Api';

import './Details.css';

const EditProducts = () => {

        const username = getUsername();

        const [editsId, setEditsId] = useState('')
        const [title, setTitle] = useState('');
        const [price, setPrice] = useState('');
        const [description, setDescription] = useState('');
        const [checked, setChecked] = useState(false);
        const [selectedFile, setSelectedFile] = useState(null);

        const {id} = useParams();
        const [edits, setEdits] = useState([]);
        const [loading, setLoading] = useState(false);

        console.log({id});

        useEffect(() => {
            const getEdits = async () => {
                setLoading(true);
                const response = await fetch(`http://localhost:1337/products/${id}`);
                setEdits(await response.json());
                setLoading(false);
                console.log(response);
            }
            getEdits();
        }, []);

        const Loading = () => {
            return(
            <>
                Loading...
            </>
            )
        }


        const changeHandler = (event) => {

        console.log("AddProducts.changeHandler event.target.files", event.target.files);

         setSelectedFile(event.target.files[0]);
    }

        const form = document.querySelector('form');




    (async function() {
            const urlPUT = `http://localhost:1337/products/${id}`;
            const token = getToken();

            try {
                const response = await fetch(urlPUT);
                const edits = await response.json();


                setTitle(edits.title);
                setPrice(edits.price);
                setDescription(edits.description);
                setChecked(edits.checked);
                setSelectedFile(edits.selectedFile);
                setEditsId(edits.id);

                console.log(editsId);

            } catch (error) {
                console.log(error);
            } 
    })();     


    function submitForm(event) {
        event.preventDefault();




         if (title.length === 0 || price.length === 0 || isNaN(price) || description.length === 0 || selectedFile.length === 0 || editsId.length === 0) {
            return console.warn("Fill inn form!");
        }

        async function updateProduct() {
            const urlPUT = `http://localhost:1337/products/${id}`;
            const token = getToken();

            const data = JSON.stringify({ title: title, price: price, description: description, featured: checked, image: selectedFile, id: editsId });
            console.log(data);



            try {
                const response = await fetch(urlPUT, {
                    method: 'PUT',
                    body: data,
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`
                    }
                });

                console.log(response);

            } catch (error) {
                console.log(error);
            }}
    }


            const ShowEdits = () => {
                            return (
            <> < h1 className = "display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2" > 
            Welcome, { username } to your Edits Page</h1> < div className = "message-container" > </div>
        <Container>
            <Form>
                <h1 className="mb-4">Edit Products</h1>
                <lable>ID</lable>
                <input className="form-control mb-4" id="id" type="text" name="editsId" value={editsId} disabled />
                <input
                    id="image"
                    className="form-control mb-4"
                    type="file"
                    accept="image/*"
                    name="selectedFile"
                    onChange={changeHandler}/>
                <div className="d-flex">
                    <h6 className="pe-5 me-5">Featured no / yes</h6>
                    <input
                        id="featured"
                        className="form-check-input mt-0 mb-4"
                        aria-label="Checkbox for following text input"
                        type="checkbox"
                        name="checked"
                        value={checked}
                        onChange={(event) => setChecked(event.target.checked)}/>
                </div>
                <lable>Title</lable>
                <input
                    id="title"
                    className="form-control mb-4"
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}/>
                    <lable>Price</lable>
                <input
                    id="price"
                    className="form-control mb-4"
                    type="number"
                    name="price"
                    placeholder="Price"
                    value={price}
                    onChange={(event) => setPrice(event.target.value)}/>
                <textarea
                    id="description"
                    className="form-control mb-4"
                    type="input"
                    name="message"
                    rows="6"
                    placeholder="Description"
                    value={description}
                    onChange={(event) => setDescription(event.target.value)}/>
                <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Verify Edit</Button>
            </Form>
        </Container>
    </>
        );
    };

    return (
        <div>
            <Container>
                <Row>
                       {loading ? <Loading /> : <ShowEdits />}
                </Row>
            </Container>
        </div>
    )
}


export default EditProducts;
