import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Container, Row, Card } from 'react-bootstrap';


/* import './product.css'; */
import { BASE_URL } from './config/api';




const Product = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setLoading] = useState(false);
    let componentMounted = true;

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            const response = await fetch(BASE_URL);

            if(componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
            }

            return () => {
                 componentMounted = false;
            }
        } 

        getProduct();
    }, []);

    const Loading = () => {
        return (
        <> 
            Loading ...
        </>
        )
    }

    const ShowProducts = () => {
        return(
            <>

            <div className="buttons d-flex justify-content-center g-4 mb-5">
                <button className="btn btn-outline-dark">Test1</button>
                <button className="btn btn-outline-dark">Test2</button>
            </div>
            {filter.map((product, index) => {
            return(
                <>

                    <div className="col-md-3 mb-4">
                    <Card className="h-100 text-center" key={product.id}>
                        <Card.Img variant="top" src={product.image_url} alt={product.title} height="250px"/>
                        <Card.Body>
                            <Card.Title>{product.title}</Card.Title>
                            <Card.Text>
                                $ {product.price},
                            </Card.Text>
                            <NavLink to={`/products/${product.id}`} className="btn btn-outline-dark">Details</NavLink>
                        </Card.Body>
                    </Card>
                    </div>
                </>
            );
        })}

            </>
        );
    };
    return (
         <div>
            <Container className="my-3 py-3">
                <Row>
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center mb-3">Our product</h1>
                        <Row className="justify-content-center">
                        {loading ? <Loading/> : <ShowProducts/>}      
                        </Row>       
                    </div>
                </Row>
            </Container>
        </div>
    );
}


export default Product;
