import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Container, Row, Card } from 'react-bootstrap';
import { getUsername } from './config/Storage';


/* import './product.css'; */
import { BASE_URL } from './config/Api';




const Product = () => {

    const username = getUsername();

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setLoading] = useState(false);
    let componentMounted = true;

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            const response = await fetch(BASE_URL + "/products");

            if(componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
            }

            return () => {
                 componentMounted = false;
            }
        } 

        getProduct();
    }, []);

    const Loading = () => {
        return (
        <> 
            Loading ...
        </>
        )
    }

    const ShowProducts = () => {
        let user = JSON.parse(localStorage.getItem('user'));

        return(
            <div>
            {/* <div className="buttons justify-content-center mb-5">
                <button className="btn btn-outline-dark me-3">Test1</button>
                <button className="btn btn-outline-dark me-3">Test2</button>
            </div> */}
            {filter.map((product) => {
            return(

                    <div className="container-show">
                    <div className="col-md-3 col-lg-9 col-xxl-12 mb-4"key={product.id}>
                    <Card className="h-100 text-center d-flex" >
                        <Card.Img variant="top" src={product.image_url} alt={product.title} height="250px"/>
                        <Card.Body>
                            <Card.Title key={Product.title}>{product.title}</Card.Title>
                            <Card.Text>
                                $ {product.price},
                            </Card.Text>
                            {
                                user ?
                                <NavLink to="/EditProducts" to={`/products/${product.id}`} className="btn btn-outline-dark">Edits</NavLink>
                                :
                                <NavLink to={`/products/${product.id}`} className="btn btn-outline-dark">Details</NavLink>
                            }
                        </Card.Body>
                    </Card>
                    </div>
                    </div>

            );
        })}

            </div>
        );
    };
    return (
        <>
        <div>
            <div className="my-3 py-3 container-md">
                <div className="row">
                    <div className="col-12 mb-5 mt-4">
                        <h1 className="display-6 fw-bolder text-center mb-4 pb-2">Our Products</h1>
                        <div className=" container-products">
                        {loading ? <Loading/> : <ShowProducts/>}      
                        </div>       
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}


export default Product;
