import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { getToken } from './config/Storage';
import displayMessage from './config/displayMessage';

import { BASE_URL } from './config/Api';
import './AdminPage.css';



import './Details.css';



const EditProducts = () => {

    const username = getUsername();




            const {id} = useParams();
            const [detail, setDetail] = useState([]);
            const [loading, setLoading] = useState(false);


            useEffect(() => {
                const getDetails = async () => {
                    setLoading(true);
                    const response = await fetch(`http://localhost:1337/products/${id}`);
                    setDetail(await response.json());
                    setLoading(false);
                    console.log("RESPONSE", detail);
                }
                getDetails();
            }, []);

            const Loading = () => {
                return(
                <>
                    Loading...
                </>
                )

            }



    }

    const ShowDetails = () => {
        return (
            <> < h1 className = "display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2" > 
            Welcome { username } to your EDIT</h1> < div className = "message-container" > </div>
        <Container>
            <Form onSubmit={handleSubmit}>
                <h1 className="mb-4">Edit your products</h1>
                <input
                    id="image"
                    className="form-control mb-4"
                    type="file"
                    accept="image/*"
                    name="selectedFile"
                    onChange={changeHandler}/>
                <div className="d-flex">
                    <h6 className="pe-5 me-5">Featured no / yes</h6>
                    <input
                        id="featured"
                        className="form-check-input mt-0 mb-4"
                        aria-label="Checkbox for following text input"
                        type="checkbox"
                        name="checked"
                        value={checked}
                        onChange={(event) => setChecked(event.target.checked)}/>
                </div>
                <input
                    id="title"
                    className="form-control mb-4"
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}/>
                <input
                    id="price"
                    className="form-control mb-4"
                    type="number"
                    name="price"
                    placeholder="Price"
                    value={price}
                    onChange={(event) => setPrice(event.target.value)}/>
                <textarea
                    id="description"
                    className="form-control mb-4"
                    type="input"
                    name="message"
                    rows="6"
                    placeholder="Description"
                    value={description}
                    onChange={(event) => setDescription(event.target.value)}/>
                <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Verify Edit</Button>

            </Form>
        </Container>
    </>
        );
    }

export default EditProducts;


