import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router';
import {Container, Row, Col, Tabs, Tab, Nav} from 'react-bootstrap'
import { Image } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';


import './Details.css';

const EditProducts = () => {

        let user = JSON.parse(localStorage.getItem('user'));

        const {id} = useParams();
        const [edits, setEdits] = useState([]);
        const [loading, setLoading] = useState(false);

        console.log({ID});

        useEffect(() => {
            const getEdits = async () => {
                setLoading(true);
                const response = await fetch(`http://localhost:1337/products/${ID}`);
                setEdits(await response.json());
                setLoading(false);
                console.log(response);
            }
            getEdits();
        }, []);

        const Loading = () => {
            return(
            <>
                Loading...
            </>
            )

        }

            const ShowEdits = () => {
                            return(
                                <>
                                    <Nav className="mr-auto">
                                        <NavLink className="nav-link back-nav" to="/products">Back to Products</NavLink>
                                    </Nav>
                                    <Container>
                                        <h3 className="mt-5">Edits page</h3>
                                        <Row className="mt-5 mb-5" >
                                            <Col className="mb-4" md={6}>
                                                <Container className="container-col1 lg-mr-auto">
                                                    <Image src={edits.image_url} fluid="fluid" />
                                                </Container>
                                            </Col>
                                            <Col md={6}>
                                                <Container className="container-col2 pb-2">
                                                    <h2>{edits.title}</h2>
                                                    <h4>Price: $ {edits.price}</h4>
                                                    <NavLink to="/Cart" variant="primary">Add to Cart</NavLink>
                                                </Container>
                                            </Col>
                                        </Row>
                                    </Container>
                                </>
                            );
                            };

    return (
        <div>
            <Container>
                <Row>
                       {loading ? <Loading /> : <ShowEdits />}
                </Row>
            </Container>
        </div>
    )
}


export default EditProducts;
