import { useState, SyntheticEvent } from 'react';
import { Form, Button } from 'react-bootstrap';
import { saveToken, saveUser } from './config/Storage';
import { URL } from './config/Api';



const Register = () => {

    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const submitForm = (event: SyntheticEvent) => {
        console.log({
            username,
            ema
        });
    }

    return (
        <Form onSubmit={submitForm}>
            <h1>Please register to login</h1>
            <input className="form-control" placeholder="Username" required 
                onChange={event => setUsername(event.target.value.trim())}
            />
            <input type="email" className="form-control" placeholder="Email" required 
                onChange={event => setEmail(event.target.value.trim())}
            />
            <input type="password" className="form-control" placeholder="Password" required 
                onChange={event => setPassword(event.target.value.trim())}
            />
            <Button className="btn btn-outline-dark" type="submit">Send to register</Button>
        </Form>
    );

}

export default Register;






async function setRegister (username, email, password) {
    const registerData = JSON.stringify({ identifier: username, email: email, password: password });


    const options = {
        method: "POST",
        body: registerData,
        headers: {"Content-Type": "application/json"},
    };

try {
    const response = await fetch(URL, options);
    const json = await response.json();

    console.log(json);

    if (json.user) {

        return("success", "You are logged in", ".message-container");
         saveToken(json.jwt);
         saveUser(json.user);



    }

    if (json.error) {
        return("warning", "Username or password is incorrect", ".message-container");
    }




} catch (error) {
    return("error", "error ", ".message-container");

}

}






