import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Container, Row, Card, Placeholder } from 'react-bootstrap';


import './Products.css';




const Featured = () => {

    const [dataFeatured, setDataFeatured] = useState([]);
    const [loading, setLoading] = useState(false);



    useEffect(() => {
        const getFeaturedProducts = async () => {
            setLoading(true);
            const response = await fetch('http://localhost:1337/' + "products");

            setDataFeatured(await response.json());

            setLoading(false);

        }
        getFeaturedProducts();
    }, []);


    const Loading = () => {
        return(
            <>
                Loading...
            </>
        )
    }
const ShowFeaturedProducts = () => {



    return(
        <>
                {dataFeatured.map((featured)=> {

        const isFeatured = featured.featured;
         if( isFeatured === true) {
                    return(
               <>
                    <div className="col-md-3 mb-4" key={featured.id}>
                    <Card className="h-100 text-center d-flex">
                        <Card.Img key={featured.image_url} variant="top" src={featured.image_url} alt={featured.title} height="250px"/>
                        <Card.Body>
                            <Card.Title key={featured.title}>{featured.title}</Card.Title>
                            <Card.Text key={featured.text}>
                                $ {featured.price} 
                            </Card.Text>
                            <NavLink key={featured.id} to={`/products/${featured.id}`} className="btn btn-outline-dark">Details</NavLink>
                        </Card.Body>
                    </Card>
                    </div>
                </> 
                )
         }else {
            return(
                    <div className="col-md-3 mb-4">
                        <Card className="h-100 text-center d-flex" aria-hidden="true">
                            <img variant="top" height="250px" alt="..." />
                                <Card.Body>
                                    <Card.Title className="placeholder placeholder-glow">
                                        <span className="placeholder col-7"></span>
                                    </Card.Title>
                                    <Card.Text className="placeholder placeholder-glow">
                                        <span className="placeholder col-7"></span>
                                    </Card.Text>
                                    <a href="#" tabindex="-1" className="btn btn-primary disabled placeholder col-6"></a>
                                </Card.Body>
                            </Card>
                        </div>
            )
         }



             })}

        </>
    )
}
    return (
        <>
        <div>
            <div className="my-3 py-3 container-md">
                <div className="row">
                    <div className="col-12 mb-5 mt-4">
                        <h1 className="display-6 fw-bolder text-center mb-4 pb-2">Featured Products</h1>
                        <div className="justify-content-between d-flex">
                        {loading ? <Loading/> : <ShowFeaturedProducts/>}      
                        </div>       
                    </div>
                </div>
            </div>
        </div>
        </>
    );

}

export default Featured;
