import React, { useState, useEffect } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { Switch } from 'antd';
import axios from "axios";

import { BASE_URL } from './config/Api';
import './AdminPage.css';

const AdminPage = () => {

     const username = getUsername();
     console.log(username);

     state = { file: null };

    const [data, setData] = useState({
        image_url: "",
        title: "",
        featured: "false",
        price: "",
        description: "",
    });    

    function handleCheck (event) {
        const newdata = { ...data }
        newdata[event.target.id] = event.target.checked;
        setData(newdata);
        console.log(newdata);
    }   

    function handleChange (event) {
        const newdata = { ...data }
        newdata[event.target.id] = event.target.value;
        setData(newdata);
        console.log(newdata);
    }    

    const submitFormUpload =  async (event) => {
        event.preventDefault();

        const data = new FormData();
        data.append('files', this.state.file, 'data', newdata)
            .post(BASE_URL + "/products", {
                image: "",
                title: setTitle,
                featured: setToggle,
                price: setPrice,
                description: setDescription
        })
            .then((response) => {
                setProduct(response.data);
                    console.log(response);
            });

    } 



    return (
        <>
            <h1 className="display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2">Welcome {username} to your admin page</h1>    
      <Container className="">
        <Form>
            <h1 className="mb-4">Upload files to Products</h1>
            <input id="image_url" className="form-control mb-4" onChange={event => handleChange(event)} type="file" name="image_url"

            />
            <div className="d-flex">
                <h6 className="pe-5 me-5">Featured no / yes</h6>
                <input id="featured" className="form-check-input mt-0 mb-4" aria-label="Checkbox for following text input" onChange={event => handleCheck(event)} type="checkbox" name="featured" value={data.featured} />
            </div>
            <input id="title" className="form-control mb-4" onChange={event => handleChange(event)} type="text" name="title" value={data.title} placeholder="Title"
            />
            <input id="price" className="form-control mb-4" onChange={event => handleChange(event)} type="int" name="price" value={data.price} placeholder="Price"
            />
            <textarea id="description" className="form-control mb-4" onChange={event => handleChange(event)} type="input" name="message" value={data.description} rows="6" placeholder="Description"
            />
             <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload Info</Button>

        </Form>
        </Container>
        </>
    );

}

export default AdminPage;
