import React, { useState, useEffect } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { Switch } from 'antd';
import axios from "axios";

import { BASE_URL } from './config/Api';
import './AdminPage.css';

const AdminPage = () => {

     const username = getUsername();
     console.log(username);

       const toggled = () => {
            toggle ? setToggle(false) : setToggle(true);
        }

    const [data, setData] = useState({
        title: "",
        featured: "",
        price: "",
        description: "",
    });    

    function handleChange (event) {

    }    

    const submitFormUpload = (event) => {
        event.preventDefault();

        axios
            .post(BASE_URL + "/products", {
                title: setTitle,
                featured: setToggle,
                price: setPrice,
                description: setDescription
        })
            .then((response) => {
                setProduct(response.data);
                    console.log(response);
            });

    }



    return (
        <>
            <h1 className="display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2">Welcome {username} to your admin page</h1>    
      <Container className="">
        <Form onSubmit={submitFormUpload}>
            <h1 className="mb-4">Upload files to Products</h1>
            <input id="" className="form-control mb-4" onChange={event => handleChange(event)} type="file" name="files" value={data.files[0]}

            />
            <div className="d-flex">
                <h6 className="pe-5 me-5">Featured no / yes</h6>
                <Switch className="mb-4" onChange={event => {handleChange(event)}, onChange={toggled}} type="checked" name="featured" value={data.featured} />
            </div>
            <input className="form-control mb-4" onChange={event => handleChange(event)} type="text" name="title" value={data.title} placeholder="Title"
            />
            <input className="form-control mb-4" onChange={event => handleChange(event)} type="int" name="price" value={data.price} placeholder="Price"
            />
            <textarea className="form-control mb-4" onChange={event => handleChange(event)} type="input" name="message" value={data.description} rows="6" placeholder="Description"
            />
             <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload Info</Button>

        </Form>
        </Container>
        </>
    );

}

export default AdminPage;
