import React, { useState, useEffect } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { Switch } from 'antd';

import './AdminPage.css';

const AdminPage = () => {

     const username = getUsername();
     console.log(username);

    const [upload, setUpload] = useState('');
    const [toggle, setToggle] = useState(false);
    const [title, setTitle] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [data, setData] = useState({title: "", Price: "", Featured: "", Description: "", Image_url: ""})


       const toggled = () => {
            toggle ? setToggle(false) : setToggle(true);
        }

    const submitFormUpload = async (event) => {
        event.preventDefault();

        console.log(data);

        const response = await fetch( URL + "/products", {
            method: 'POST',
             headers: {'Content-Type': 'application/json'},
            body: data,
        })


        const file = await response.json();

    }


    function handler(event) {
        const newData = { ...data };
        newData[event.target.id] = event.target.value;
        setData(newData);
        console.log(newData);
    }


    return (
        <>
            <h1 className="display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2">{username} welcome to your admin page</h1>    
      <Container className="">
        <Form onSubmit={submitFormUpload}>
            <h1 className="mb-4">Upload files to Products</h1>
            <input type="file" className="form-control mb-4" name="files"
                onChange={(event) => handler(event)} files={data.Image_url}
            />
            <div className="d-flex">
                <h6 className="pe-5 me-5">Featured no / yes</h6>
                <Switch className="mb-4" onChange={(event) => {handler(event); this.toggled()}} value={data.Featured} /> 
            </div>
            <input type="text" className="form-control mb-4" name="title" placeholder="Title"
                onChange={(event) => handler(event)} value={data.title}
            />
            <input type="int" className="form-control mb-4" name="price" placeholder="Price"
                onChange={(event) => handler(event)} value={data.Price}
            />
            <textarea type="input" className="form-control mb-4" name="message" rows="6" placeholder="Description"
                onChange={(event) => handler(event)} value={data.description}
            />
             <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload Info</Button>

        </Form>
        </Container>
        </>
    );

}

export default AdminPage;
