import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Container, Row, Card } from 'react-bootstrap';


import './Products.css';




const Featured = () => {

    const [dataFeatured, setDataFeatured] = useState([]);
    const [loading, setLoading] = useState(false);




    useEffect(() => {
        const getFeaturedProducts = async () => {
            setLoading(true);
            const response = await fetch('http://localhost:1337/' + "products");

            setDataFeatured(await response.json());
            setLoading(false);


        }
        getFeaturedProducts();
    }, []);


    const Loading = () => {
        return(
            <>
                Loading...
            </>
        )
    }
const ShowFeaturedProducts = () => {

         if (featuredTrue === true) {
                    <Card.Text>Featured product</Card.Text>
                } else {
                    <Card.Text></Card.Text>
                }


    return(
        <>
               {dataFeatured.map((featured)=> {
                console.log(featured);
                const featuredTrue = JSON.stringify(featured.featured);



                return(
               <>
               {featured.featured === true}
                    <div className="col-md-3 mb-4" key={featured.id}>
                    <Card className="h-100 text-center d-flex">
                        <Card.Img key={featured.image_url} variant="top" src={featured.image_url} alt={featured.title} height="250px"/>
                        <Card.Body>
                            <Card.Title key={featured.title}>{featured.title}</Card.Title>
                            <Card.Text key={featured.text}>
                                $ {featured.price}
                            </Card.Text>
                            <Card.Text>{featuredTrue}</Card.Text>
                            <NavLink key={featured.id} to={`/products/${featured.id}`} className="btn btn-outline-dark">Details</NavLink>
                        </Card.Body>
                    </Card>
                    </div>
                </>

                )
             })}
        </>
    )
}
    return (
        <>
        <div>
            <Container className="my-5 py-5">
                <Row>
                    <div className="col-12 mb-5 mt-4">
                        <h1 className="display-6 fw-bolder text-center">Featured Products</h1>
                        <Row className="justify-content-center">
                        {loading ? <Loading/> : <ShowFeaturedProducts/>}      
                        </Row>       
                    </div>
                </Row>
            </Container>
        </div>
        </>
    );

}

export default Featured;
