import React, {useState, useEffect} from 'react';
import {NavLink} from 'react-router-dom';
import {Container, Row, Card} from 'react-bootstrap';

import './Products.css';

const Featured = () => {

    const [dataFeatured, setDataFeatured] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const getFeaturedProducts = async () => {
            setLoading(true);
            const response = await fetch('http://localhost:1337/products');

            setDataFeatured(await response.json());

            setLoading(false);

        }
        getFeaturedProducts();
    }, []);

    const Loading = () => {
        return (<> Loading ...</>)
    }
    const ShowFeaturedProducts = () => {

        return (
            <> 
            {
                dataFeatured.map((featured) => {

                    const isFeatured = featured.featured;
                    if (isFeatured === true) {
                        return (
                            <> < div className = "col-md-3 mb-4" key = {
                                featured.id
                            } > <Card className="h-100 text-center d-flex">
                                <Card.Img
                                    key={featured.image_url}
                                    variant="top"
                                    src={featured.image_url}
                                    alt={featured.title}
                                    height="250px"/>
                                <Card.Body>
                                    <Card.Title key={featured.title}>{featured.title}</Card.Title>
                                    <Card.Text key={featured.text}>
                                        $ {featured.price}
                                    </Card.Text>
                                    <NavLink
                                        key={featured.id}
                                        to={`/products/${featured.id}`}
                                        className="btn btn-outline-dark">Details</NavLink>
                                </Card.Body>
                            </Card>
                        </div>
                    </>
                        )
                    } else {
                        console.log('');
                    }

                })
            } < />
    )
}
    return (
        <>
        <div>
            <div className="my-3 py-3 container-md">
                <div className="row">
                    <div className="col-12 mb-5 mt-4">
                        <h1 className="display-6 fw-bolder text-center mb-4 pb-2">Featured Products</h1 > <div className="justify-content-between d-flex">
                {
                    loading
                        ? <Loading/>
                        : <ShowFeaturedProducts/>
                }
            </div>
        </div>
    </div>
</div>
</div>
</>
        );

    }

    export default Featured;
