import React from 'react';
import { useState } from 'react';
import { Form, Button, Container } from 'react-bootstrap';

import './Contact.css';

export const Contact = () => {


    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

        console.log(firstName);
        console.log(lastName);
        console.log(email);
        console.log(message);

 function saveToStorage(key, value) {
    localStorage.setItem(key, firstName);
    // localStorage.setItem(JSON.stringify(setFirstName)); 
}


function store(){ //stores items in the localStorage



    window.localStorage.setItem(JSON.stringify(setFirstName));  
    //converting object to string
}







    return (
            <section className='contact' id='contact'>
            <div className='section-title'>
                <h2>Contact</h2>
            </div>
            <Container className='d-flex justify-content-center flex-wrap-wrap mt-5 card-flex'>
        <Form className="w-50" >
            <h1>Contact Me!</h1>
            <input type="firstName" id='firstName' className="form-control mb-3" placeholder="firstName" required 
                onChange={event => setFirstName(event.target.value.trim())}
            />  
            <input type="lastName" className="form-control mb-3" placeholder="lastName" required 
                onChange={event => setLastName(event.target.value.trim())}
            />                         
            <input type="email" className="form-control mb-3" placeholder="Email" required 
                onChange={event => setEmail(event.target.value.trim())}
            />
            <input type="message" className="form-control mb-3" placeholder="Message" required 
                onChange={event => setMessage(event.target.value.trim())}
            />
            <Button className="btn btn-outline-dark text-white" type="submit">Contact Me</Button>
        </Form>
        </Container>
        </section>
    );

    }
