import { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { saveToken, saveUser } from './config/Storage';
import { BASE_URL } from './config/Api';
import { Redirect } from 'react-router';


const Register = () => {


    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmationPassword, setConfirmationPassword] = useState('');
    const [redirect, setRedirect] = useState(false);



const submitForm = async (event) => {
        event.preventDefault();

        const register = await fetch( BASE_URL + "/auth/local", {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ identifier: email, password})
        });

        const regontent = await response.json();


      if (content.user) {
        saveToken(content.jwt);
        saveUser(content.user);

    }

   setRedirect(true);
   window.location.reload();
    }

 if (redirect) {
        return <Redirect to="/Login" />
    }



    return (
        <Form onSubmit={submitForm}>
            <h1>Please login</h1>
            <input type="name" className="form-control" placeholder="Name" required 
                onChange={event => setUsername(event.target.value.trim())}
            />            
            <input type="email" className="form-control" placeholder="Email" required 
                onChange={event => setEmail(event.target.value.trim())}
            />
            <input type="password" className="form-control" placeholder="Password" required 
                onChange={event => setPassword(event.target.value.trim())}
            />
            <Button className="btn btn-outline-dark" type="submit">Register</Button>
        </Form>
    );

    }


export default Register;





