import React, { useState, useForm } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { getToken } from './config/Storage';
import displayMessage from './config/displayMessage';

import { BASE_URL } from './config/Api';
import './AdminPage.css';







const AddProducts = () => {

    const username = getUsername();

    const [title, setTitle] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [checked, setChecked] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);

    console.log(title);
    console.log(price);
    console.log(description);
    console.log(checked);
    console.log(selectedFile);

function readURL(input) {
            if (input.files && input.files[0]) {
                var reader = new FileReader();

                reader.onload = function (event) {
                        console.log(event.target.result);
                };

                reader.readAsDataURL(input.files[0]);
            }
        }



    const changeHandler = (event) => {
        let selectedFileUpload = event.target.files[0];
        selectedFileUpload = new FormData();
        setSelectedFile(selectedFileUpload);
    }


    const handleSubmit = (event) => {
        event.preventDefault();
        event
            .target
            .reset();

        if (title.length === 0 || price.length === 0 || isNaN(price) || description.length === 0 || selectedFile.length === 0) {
            return console.warn("Fill inn form!");
        }

        ((async function addProduct() {
            const urlAdd = BASE_URL + "/products";

            // const data = new FormData();
            // data.append('files', {
            //     type: 'multipart/form-data'
            // });

            const data = JSON.stringify({ title: title, price: price, description: description, featured: checked, image: selectedFile });
            console.log(data);
            const token = getToken();

            try {
                const response = await fetch(urlAdd, {
                    method: 'POST',
                    body: data,
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`
                    }
                });

                console.log(response);

            } catch (error) {
                console.log(error);
            }
        }()))
        }

        return (
            <> < h1 className = "display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2" > 
            Welcome { username } to your admin page</h1> < div className = "message-container" > </div>
        <Container>
            <Form onSubmit={handleSubmit}>
                <h1 className="mb-4">Upload files to Products</h1>
                <input
                    id="image"
                    className="form-control mb-4"
                    type="file"
                    accept="image/*"
                    name="selectedFile"
                    onChange="readURL(this)"/>
                <div className="d-flex">
                    <h6 className="pe-5 me-5">Featured no / yes</h6>
                    <input
                        id="featured"
                        className="form-check-input mt-0 mb-4"
                        aria-label="Checkbox for following text input"
                        type="checkbox"
                        name="checked"
                        value={checked}
                        onChange={(event) => setChecked(event.target.checked)}/>
                </div>
                <input
                    id="title"
                    className="form-control mb-4"
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}/>
                <input
                    id="price"
                    className="form-control mb-4"
                    type="number"
                    name="price"
                    placeholder="Price"
                    value={price}
                    onChange={(event) => setPrice(event.target.value)}/>
                <textarea
                    id="description"
                    className="form-control mb-4"
                    type="input"
                    name="message"
                    rows="6"
                    placeholder="Description"
                    value={description}
                    onChange={(event) => setDescription(event.target.value)}/>
                <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload product</Button>

            </Form>
        </Container>
    </>
        );
}
export default AddProducts;


