import React, { useState, useEffect } from 'react';
import { Button, Form, Container } from 'react-bootstrap';
import { getUsername } from './config/Storage';
import { getToken } from './config/Storage';
import displayMessage from './config/displayMessage';

import { BASE_URL } from './config/Api';
import './AdminPage.css';



const AddProducts = () => {

    const username = getUsername();


    const form = document.querySelector('form');
    const title = document.querySelector('#title');
    const price = document.querySelector('#price');
    const description = document.querySelector('#description');
    const featured = document.querySelector('#featured');
    const image = document.querySelector('#image');
    const url = document.querySelector('#url');
    const message = document.querySelector('.message-container');    


    form.addEventListener("submit", submitForm);


    const submitForm =  async (event) => {
        event.preventDefault();

        const titleValue = title.value.trim();
        const priceValue = parseFloat(price.value);
        const descriptionValue = description.value.trim();
        const featuredValue = featured.value.checked;
        const imageValue = image.value.trim();
        const urlValue = url.value.trim();

        if (titleValue.length === 0 || priceValue.length === 0 || isNaN(priceValue) || descriptionValue.length === 0 || featuredValue.length === 0) {
            return displayMessage("warning", "Values are incorrect", ".message-container");
        } 

        addProduct(titleValue, priceValue, descriptionValue, featuredValue, imageValue);

    }
        async function addProduct(title, price, description, featured, image, url) {
            const urlAdd = BASE_URL + "/products";
            const data = JSON.stringify({ title: title, price: price, description: description, featured: featured, image: image, url: url });   

            const token = getToken();

            const options = {
                method: 'POST',
                body: data,
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`                },
            };

            try {
                const response = await fetch(urlAdd, options);
                const json = response.json();

                console.log(json);
            }
            catch(error) {
                console.log(error);
            }

        } 



    return (
        <>

            <h1 className="display-6 fw-bolder text-center mb-4 pt-4 mt-4 pt-4 pb-2">Welcome {username} to your admin page</h1>   
            <div className="message-container"></div>
      <Container>
        <Form On>
            <h1 className="mb-4">Upload files to Products</h1>
            <input id="image" className="form-control mb-4" type="file" name="image" />
            <div className="d-flex">
                <h6 className="pe-5 me-5">Featured no / yes</h6>
                <input id="featured" className="form-check-input mt-0 mb-4" aria-label="Checkbox for following text input" type="checkbox" name="featured" />
            </div>
            <input id="title" className="form-control mb-4" type="text" name="title" placeholder="Title"
            />
            <input id="price" className="form-control mb-4" type="int" name="price" placeholder="Price"
            />
            <textarea id="description" className="form-control mb-4" type="input" name="message" rows="6" placeholder="Description"
            />
             <Button className="btn btn-outline-dark text-light fw-bold" type="submit">Upload Info</Button>

        </Form>
        </Container>
        </>
    );

}

export default AddProducts;


