import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { Container, Row, Card } from 'react-bootstrap';


/* import './product.css'; */
import { BASE_URL } from './config/api';




const Product = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setLoading] = useState(false);
    let componentMounted = true;

    useEffect(() => {
        const getProduct = async () => {
            setLoading(true);
            const response = await fetch(BASE_URL);

            if(componentMounted) {
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
            }

            return () => {
                 componentMounted = false;
            }
        } 

        getProduct();
    }, []);

    const Loading = () => {
        return (
        <> 
            Loading ...
        </>
        )
    }

    const ShowProducts = () => {
        return(
            <>
            <Container>
            <div className="filter-buttons d-flex justify-content-center mb-5">
                <button className="btn btn-outline-dark me-4">Test1</button>
                <button className="btn btn-outline-dark me-4">Test2</button>
                <button className="btn btn-outline-dark me-4">Test3</button>
                <button className="btn btn-outline-dark me-4">Test4</button>
                <button className="btn btn-outline-dark me-4">Test5</button>
            </div>
            {filter.map((product, index) => {
            return(
                <>

                    <div className="col-md-3 mb-4">
                    <Card className="h-100 text-center" key={product.id}>
                        <Card.Img variant="top" src={product.image_url} alt={product.title} height="250px"/>
                        <Card.Body>
                            <Card.Title>{product.title}</Card.Title>
                            <Card.Text>
                                $ {product.price},
                            </Card.Text>
                            <NavLink to={`/products/${product.id}`} className="btn btn-outline-dark">Details</NavLink>
                        </Card.Body>
                    </Card>
                    </div>
                </>
            );
        })}
        </Container>
            </>
        );
    };
    return (
         <div>
            <Container className="my-5 py-5">
                <Row>
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center">Our product</h1>
                        <Row className="justify-content-center">
                        {loading ? <Loading/> : <ShowProducts/>}      
                        </Row>       
                    </div>
                </Row>
            </Container>
        </div>
    );
}


export default Product;
